mapper.select:
SELECT * 
FROM SELLER
WHERE SELLER_ID = #{sellerId}
AND SELLER_PASSWORD = #{sellerPassword}

SELECT * 
FROM SELLER
WHERE SELLER_ID = #{sellerId}
