mapper.select:
SELECT * 
FROM SELLER
WHERE SELLER_ID = #{sellerId}
AND COMPANY_NAME = #{companyName}
AND CUSTOMER_EMAIL = #{customerEmail}
AND REGISTRATION_NUM = #{registrationNum}

